mapper.insert:
INSERT INTO member (
    id, username, password, email, city_code, city_etc, role
) VALUES (
    #{id}, #{username}, #{password}, #{email}, #{city_code}, #{city_etc}, #{role}
)

mapper.select:
SELECT id, password, username, email, city_code, city_etc, role, appr_yn
FROM member
WHERE id = #{id}

mapper.update:
UPDATE member
SET appr_yn = #{appr_yn}
WHERE id = #{id}

UPDATE member
SET role = #{role}
WHERE id = #{id}

mapper.select:
SELECT id
FROM member
WHERE username = #{name}
  AND email = #{email}

SELECT *
FROM member
WHERE id = #{userId}
  AND email = #{email}

mapper.update:
UPDATE member
SET password = #{encodedPassword}
WHERE id = #{userId}

UPDATE member
SET username = #{username},
    email = #{email},
    city_code = #{city_code},
    city_etc = #{city_etc}
WHERE id = #{id}

UPDATE board_master
SET board_master_name = #{boardMasterName}
WHERE board_master_code = #{boardMasterCode}

UPDATE board_master
SET board_use_yn = #{boardUseYn}
WHERE board_master_code = #{boardMasterCode}

mapper.select:
SELECT board_master_code
FROM board_master
WHERE board_link = #{fullUri}

SELECT read_auth
FROM board_auth
WHERE user_type = #{userType}
  AND board_master_code_id = #{boardMasterCode}
